from week_2.caesar import caesar_encrypt_decrypt
from week_2.vigenere import vigenere_encrypt_decrypt, EncryptionMode


def test_caesar_encryption_lower():
    message = 'abcdefghijklmnopqrstuvwxyz'
    enc = 'defghijklmnopqrstuvwxyzabc'
    assert caesar_encrypt_decrypt(message, 3) == enc
    assert caesar_encrypt_decrypt(enc, -3) == message


def test_caesar_encryption_upper():
    message = 'ABCDEFGHIJKLMNOPQRSTUVWXYZ'
    enc = 'DEFGHIJKLMNOPQRSTUVWXYZABC'
    assert caesar_encrypt_decrypt(message, 3) == enc
    assert caesar_encrypt_decrypt(enc, -3) == message


def test_caesar_encryption_number():
    message = '1234567890'
    enc = '4567890123'
    assert caesar_encrypt_decrypt(message, 3) == enc
    assert caesar_encrypt_decrypt(enc, -3) == message


def test_caesar_encryption_all():
    message = 'abcdefghijklmnopqrstuvwxyzABCDEFGHIJKLMNOPQRSTUVWXYZ1234567890'
    enc = ''
    summary = []
    for i in range(-50, 50):
        summary.append(caesar_encrypt_decrypt(message, i))
    assert ''.join(summary) == enc


def test_vigenere_encryption_lower():
    message = 'abcdefghijklmnopqrstuvwxyz'
    enc = 'bdfhfhjljlnpnprtrtvxvxzbzb'
    assert vigenere_encrypt_decrypt(message, 'bcde', EncryptionMode.ENC) == enc
    assert vigenere_encrypt_decrypt(enc, 'bcde', EncryptionMode.DEC) == message


def test_vigenere_encryption_upper():
    message = 'ABCDEFGHIJKLMNOPQRSTUVWXYZ'
    enc = 'BDFHFHJLJLNPNPRTRTVXVXZBZB'
    assert vigenere_encrypt_decrypt(message, 'bcde', EncryptionMode.ENC) == enc
    assert vigenere_encrypt_decrypt(enc, 'bcde', EncryptionMode.DEC) == message


def test_vigenere_encryption_number():
    message = '1234567890'
    enc = '2468680202'
    assert vigenere_encrypt_decrypt(message, 'bcde', EncryptionMode.ENC) == enc
    assert vigenere_encrypt_decrypt(enc, 'bcde', EncryptionMode.DEC) == message


def test_vigenere_encryption_all():
    message = 'abcdefghijklmnopqrstuvwxyzABCDEFGHIJKLMNOPQRSTUVWXYZ1234567890'
    enc = 'bdfhfhjljlnpnprtrtvxvxzbzbDFDFHJHJLNLNPRPRTVTVXZXZBD2468680202'
    assert vigenere_encrypt_decrypt(message, 'bcde', EncryptionMode.ENC) == enc
    assert vigenere_encrypt_decrypt(enc, 'bcde', EncryptionMode.DEC) == message
